import { createTheme } from "@mui/material/styles";

export const Colors = {
    primary: "#F1F0ED",
    secondary: "67645E",
};

const theme = createTheme({
    palette : { 
        primary: {
            main: Colors.primary
        },
        secondary: {
            main: Colors.secondary 
        }
    },
    components : {  
        MuiButton: {
            defaultProps: {
                disableRipple: true,
                disableElevation: true
            }
        }
    }});
